def factorial(n):
 if n == 0:
 return 1
 return n * factorial(n-1)

def fibonacci(n):
 if n <= 0:
 return 0
 elif n == 1:
 return 1
 else:
 return fibonacci(n-1) + fibonacci(n-2)

def prime_factors(n):
 factors = []
 divisor = 2
 while divisor <= n:
 if n % divisor == 0:
 factors.append(divisor)
 n = n // divisor
 else:
 divisor += 1
 return factors
